--TO LOAD THIS DATABASE INTO PSQL, CREATE A DATABASE ("CREATE DATABASE hunger")
--Then, in command line (not in PSQL) enter "sudo -u postgres psql hunger < Hunger_Database.sql"


DROP TABLE IF EXISTS restaurants;
DROP TABLE IF EXISTS food;
DROP TABLE IF EXISTS client;
DROP TABLE IF EXISTS purchases;


--
-- Name: restaurants;
--

CREATE TABLE restaurants (
    id INT PRIMARY KEY,
    restaurant_name VARCHAR(30) NOT NULL,
    restaurant_type VARCHAR(30) NOT NULL,
    restaurant_link VARCHAR(30),
    description VARCHAR(200),
    address VARCHAR(60),
	  lat DECIMAL(10, 6) NOT NULL,
  	lng DECIMAL(10, 6) NOT NULL,
    avg_price VARCHAR(5),
  	avg_rating FLOAT,
  	num_ratings int,
  	menu INT[] NOT NULL
);

--
-- Name: food;
--

CREATE TABLE food (
	  id INT PRIMARY KEY,
  	item_name VARCHAR(30),
  	price FLOAT
);


--
-- Name: purchases;
--

CREATE TABLE purchases (
    id INT PRIMARY KEY ,
    order_date DATE NOT NULL,
    tip FLOAT,
  	tax FLOAT,
  	delivery FLOAT,
  	order_rating int,
  	food_ids INT[]
);


--
-- Name: clients;
--

CREATE TABLE clients (
    id INT PRIMARY KEY,
  	first_name VARCHAR(30) NOT NULL,
  	last_name VARCHAR(30) NOT NULL,
  	username VARCHAR(30) NOT NULL,
  	hashed_password VARCHAR(50) NOT NULL,
  	purchase_ids INT[]
);



